CodeChunks.CodeChunk:
// Stop and dismount
if (Core.Player.IsMounted)
{
  await CommonTasks.StopAndDismount();
  await Coroutine.Sleep(500);
}

// Dive
MovementManager.StartDescending();
await Coroutine.Wait(10000, () => MovementManager.IsDiving);

// Stop movement
MovementManager.MoveStop();
await Coroutine.Sleep(500);

// Mount up
if (!Core.Player.IsMounted)
{
  await CommonTasks.SummonFlyingMount();
  await Coroutine.Wait(10000, () => Core.Player.IsMounted);
}

// Descend
await CommonTasks.DescendTo(-109f);

// Stop and dismount
await CommonTasks.StopAndDismount();

// Face the Fishing Rod	   
GameObjectManager.GetObjectByNPCId(2008799).Face();

// Target and interact with the Fishing Rod
if (GameObjectManager.GetObjectByNPCId(2008799).IsVisible)
{
  GameObjectManager.GetObjectByNPCId(2008799).Target();
  GameObjectManager.GetObjectByNPCId(2008799).Interact();
}

// Skip dialog and wait until step 4
while (ConditionParser.GetQuestStep(68284) != 4)
{
  if (Talk.DialogOpen)
  {
    Talk.Next();
  }
  await Coroutine.Yield();
}

// Pause
await Coroutine.Sleep(2000);

if (ConditionParser.GetQuestStep(68284) == 4 && MovementManager.IsDiving)
{
  // Mount up
  if (!Core.Player.IsMounted)
  {
    await CommonTasks.SummonFlyingMount();
    await Coroutine.Sleep(500);
  }

  // Ascend
  MovementManager.StartAscending();
  await Coroutine.Wait(30000, () => !MovementManager.IsDiving);
}

// Stop movement
MovementManager.MoveStop();

// Dismount
await CommonTasks.StopAndDismount();

// Dismount just in case FlyToTag forgets to
if (Core.Player.IsMounted)
{
  await CommonTasks.StopAndDismount();
  await Coroutine.Sleep(500);
}

// Say "his whiskers"
ChatManager.SendChat("/say his whiskers");

// Pause
await Coroutine.Sleep(2000);

// Skip dialog whilst occupied and movement locked
while (Talk.ConvoLock || MovementManager.IsOccupied)
{
  if (Talk.DialogOpen)
  {
    Talk.Next();
  }
  await Coroutine.Yield();
}
